mapper.update:
update lpt_category
category_name = #{name}, category_type = #{type}, category_state = #{state}, category_intro = #{intro}, category_cover_img = #{coverImg}, category_parent_id = #{parentId}, category_disp_seq = #{dispSeq}, category_cache_num = #{cacheNum},
where category_id = #{id}

update lpt_category
set category_top_post_id = #{postId}
where category_id = #{categoryId}

